import React, { useState, useEffect, useContext, useCallback } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import { UserContext } from '../context/UserContext';
import PredictionItem from '../components/PredictionItem';
import config from '../config';
import './SessionPage.css';

const SessionPage = () => {
  const [session, setSession] = useState(null);
  const [prediction, setPrediction] = useState('');
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [copied, setCopied] = useState(false);
  const [refreshKey, setRefreshKey] = useState(0);
  
  const { id } = useParams();
  const { user } = useContext(UserContext);
  
  // ÿ¨ŸÑÿ® ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¨ŸÑÿ≥ÿ© ŸÖÿπ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ useCallback
  const fetchSession = useCallback(async () => {
    try {
      setLoading(true);
      
      // ÿ™ŸÉŸàŸäŸÜ ÿßŸÑŸáŸäÿØÿ±
      const headers = {
        headers: {
          Authorization: `Bearer ${user.token}`
        }
      };
      
      // ÿ¨ŸÑÿ® ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      const { data } = await axios.get(`${config.API_URL}/sessions/${id}`, headers);
      
      setSession(data.session);
      setLoading(false);
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¨ŸÑÿ≥ÿ©:', error);
      setErrorMessage('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ¨ŸÑÿ® ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¨ŸÑÿ≥ÿ©');
      setLoading(false);
    }
  }, [id, user.token]);
  
  // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿπŸÜÿØ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿµŸÅÿ≠ÿ© ÿ£Ÿà ÿ™ÿ∫ŸäŸäÿ± ŸÖŸÅÿ™ÿßÿ≠ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´
  useEffect(() => {
    fetchSession();
  }, [fetchSession, refreshKey]);
  
  // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ£ŸÜ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÇÿØŸÖ ÿ™ŸàŸÇÿπŸãÿß ÿ®ÿßŸÑŸÅÿπŸÑ
  const hasSubmittedPrediction = () => {
    if (!session || !session.predictions) return false;
    
    const hasSubmitted = session.predictions.some(
      prediction => prediction.user._id === user._id
    );
    
    return hasSubmitted;
  };
  
  // ŸÜÿ≥ÿÆ ŸÉŸàÿØ ÿßŸÑÿ¨ŸÑÿ≥ÿ©
  const copySessionCode = () => {
    if (!session) return;
    
    navigator.clipboard.writeText(session.code)
      .then(() => {
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
      })
      .catch(err => {
        console.error('ŸÅÿ¥ŸÑ ŸÅŸä ŸÜÿ≥ÿÆ ÿßŸÑŸÜÿµ:', err);
      });
  };
  
  // ŸÖÿπÿßŸÑÿ¨ÿ© ÿ™ŸÇÿØŸäŸÖ ÿßŸÑÿ™ŸàŸÇÿπ
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ•ÿØÿÆÿßŸÑ ÿßŸÑÿ™ŸàŸÇÿπ
    if (!prediction.trim()) {
      setErrorMessage('Ÿäÿ±ÿ¨Ÿâ ÿ•ÿØÿÆÿßŸÑ ÿ™ŸàŸÇÿπŸÉ');
      return;
    }
    
    try {
      setSubmitting(true);
      setErrorMessage('');
      
      // ÿ™ŸÉŸàŸäŸÜ ÿßŸÑŸáŸäÿØÿ±
      const headers = {
        headers: {
          Authorization: `Bearer ${user.token}`
        }
      };
      
      // ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ™ŸàŸÇÿπ
      const { data } = await axios.post(
        `${config.API_URL}/sessions/predict`,
        { sessionId: id, text: prediction },
        headers
      );
      
      // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ¨ŸÑÿ≥ÿ© ÿßŸÑŸÖÿ≠ŸÑŸäÿ©
      setSession(data.session);
      setSuccessMessage('ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ™ŸàŸÇÿπŸÉ ÿ®ŸÜÿ¨ÿßÿ≠');
      setPrediction('');
      setSubmitting(false);
      
      // ÿ™ÿ≠ÿØŸäÿ´ ŸÖŸÅÿ™ÿßÿ≠ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ ŸÑÿ•ÿ¨ÿ®ÿßÿ± ÿßŸÑÿµŸÅÿ≠ÿ© ÿπŸÑŸâ ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ
      setRefreshKey(oldKey => oldKey + 1);
      
      // ÿ•ÿπÿßÿØÿ© ÿ¨ŸÑÿ® ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¨ŸÑÿ≥ÿ© ŸÑŸÑÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿßÿ≥ÿ™ŸÑÿßŸÖ ÿ£ÿ≠ÿØÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      setTimeout(() => {
        fetchSession();
      }, 500);
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ™ŸàŸÇÿπ:', error);
      setErrorMessage(error.response?.data?.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ™ŸàŸÇÿπ');
      setSubmitting(false);
    }
  };
  
  // ÿ™ŸÜÿ≥ŸäŸÇ ÿßŸÑÿ™ÿßÿ±ŸäÿÆ
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString('ar-SA', options);
  };
  
  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p className="loading-text">ÿ¨ÿßÿ±Ÿê ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¨ŸÑÿ≥ÿ©...</p>
      </div>
    );
  }
  
  if (!session) {
    return (
      <div className="error-container">
        <div className="error-icon">‚ö†Ô∏è</div>
        <h2>ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¨ŸÑÿ≥ÿ©</h2>
        <p>{errorMessage}</p>
        <Link to="/dashboard" className="btn btn-primary">
          ÿßŸÑÿπŸàÿØÿ© ÿ•ŸÑŸâ ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ
        </Link>
      </div>
    );
  }

  // ŸáŸÑ ŸÇÿØŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑÿ≠ÿßŸÑŸä ÿ™ŸàŸÇÿπŸá
  const userHasPredicted = hasSubmittedPrediction();
  
  return (
    <div className="session-page">
      <div className="session-header">
        <h1 className="session-title">{session.title}</h1>
        
        <div className="session-meta">
          <div className="session-code-container">
            <span className="meta-label">ŸÉŸàÿØ ÿßŸÑÿ¨ŸÑÿ≥ÿ©:</span>
            <span className="session-code">{session.code}</span>
            <button 
              className="copy-btn" 
              onClick={copySessionCode}
              title="ŸÜÿ≥ÿÆ ÿßŸÑŸÉŸàÿØ"
            >
              {copied ? '‚úì' : 'üìã'}
            </button>
          </div>
          
          <div className="session-info date">
            <span className="meta-label">ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿ•ŸÜÿ¥ÿßÿ°:</span>
            <span>{formatDate(session.createdAt)}</span>
          </div>
          
          <div className="session-info participants">
            <span className="meta-label">ÿßŸÑŸÖÿ¥ÿßÿ±ŸÉŸàŸÜ:</span>
            <span>{session.participants.length}/{session.maxPlayers}</span>
          </div>
          
          <div className="session-status-container">
            <span className="meta-label">ÿßŸÑÿ≠ÿßŸÑÿ©:</span>
            <span className={`session-status ${session.isComplete ? 'complete' : 'active'}`}>
              {session.isComplete ? 'ŸÖŸÉÿ™ŸÖŸÑÿ©' : 'ŸÜÿ¥ÿ∑ÿ©'}
            </span>
          </div>
        </div>
      </div>
      
      <div className="session-content">
        {/* ÿπÿ±ÿ∂ ŸÜŸÖŸàÿ∞ÿ¨ ÿßŸÑÿ™ŸàŸÇÿπ ÿ•ÿ∞ÿß ŸÑŸÖ ŸäŸÉŸÜ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÇÿØ ŸÇÿØŸÖ ÿ™ŸàŸÇÿπŸãÿß ÿ®ÿπÿØ */}
        {!userHasPredicted && (
          <div className="prediction-form-container">
            <h2 className="section-title prediction">ÿ£ÿØÿÆŸÑ ÿ™ŸàŸÇÿπŸÉ</h2>
            
            {errorMessage && (
              <div className="alert alert-error">{errorMessage}</div>
            )}
            
            {successMessage && (
              <div className="alert alert-success">{successMessage}</div>
            )}
            
            <form onSubmit={handleSubmit} className="prediction-form">
              <div className="form-group">
                <textarea
                  className="form-control prediction-textarea"
                  value={prediction}
                  onChange={(e) => setPrediction(e.target.value)}
                  placeholder="ÿßŸÉÿ™ÿ® ÿ™ŸàŸÇÿπŸÉ ŸáŸÜÿß..."
                  rows={4}
                ></textarea>
              </div>
              
              <button 
                type="submit" 
                className="btn btn-primary submit-prediction-btn"
                disabled={submitting}
              >
                {submitting ? 'ÿ¨ÿßÿ±Ÿê ÿßŸÑÿ•ÿ±ÿ≥ÿßŸÑ...' : 'ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ™ŸàŸÇÿπ'}
              </button>
            </form>
            
            {/* ÿ±ÿ≥ÿßŸÑÿ© ÿ•ÿÆÿ®ÿßÿ±Ÿäÿ© ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ®ÿ∂ÿ±Ÿàÿ±ÿ© ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ™ŸàŸÇÿπ ÿ£ŸàŸÑÿßŸã */}
            <div className="alert-info mt-4">
              Ÿäÿ¨ÿ® ÿπŸÑŸäŸÉ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ™ŸàŸÇÿπŸÉ ÿ£ŸàŸÑÿßŸã ŸÇÿ®ŸÑ ÿ£ŸÜ ÿ™ÿ™ŸÖŸÉŸÜ ŸÖŸÜ ŸÖÿ¥ÿßŸáÿØÿ© ÿ™ŸàŸÇÿπÿßÿ™ ÿßŸÑŸÖÿ¥ÿßÿ±ŸÉŸäŸÜ ÿßŸÑÿ¢ÿÆÿ±ŸäŸÜ
            </div>
          </div>
        )}
        
        {/* ÿπÿ±ÿ∂ ÿßŸÑÿ™ŸàŸÇÿπÿßÿ™ ŸÅŸÇÿ∑ ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÇÿØ ŸÇÿØŸÖ ÿ™ŸàŸÇÿπŸãÿß */}
        {userHasPredicted && (
          <div className="predictions-container">
            <h2 className="section-title predictions">
              ÿßŸÑÿ™ŸàŸÇÿπÿßÿ™
              <span className="predictions-count">
                {session.predictions.length}/{session.maxPlayers}
              </span>
            </h2>
            
            {session.predictions.length === 0 ? (
              <div className="empty-predictions">
                <div className="empty-predictions-icon">üìù</div>
                <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ™ŸàŸÇÿπÿßÿ™ ÿ≠ÿ™Ÿâ ÿßŸÑÿ¢ŸÜ.</p>
              </div>
            ) : (
              <div className="predictions-list">
                {session.predictions.map((prediction) => (
                  <PredictionItem 
                    key={prediction._id || Math.random().toString()} 
                    prediction={prediction}
                    isCurrentUser={prediction.user._id === user._id}
                  />
                ))}
              </div>
            )}
          </div>
        )}
        
        {/* ÿ•ÿ∏Ÿáÿßÿ± ÿ±ÿ≥ÿßŸÑÿ© ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿπŸÜÿØ ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ™ŸàŸÇÿπ ŸÅŸä ÿ≠ÿßŸÑÿ© ÿπÿØŸÖ Ÿàÿ¨ŸàÿØ ÿ™ŸàŸÇÿπÿßÿ™ ÿ£ÿÆÿ±Ÿâ */}
        {userHasPredicted && session.predictions.length <= 1 && (
          <div className="alert-info mt-4">
            ÿ™ŸàŸÇÿπÿßÿ™ ÿßŸÑŸÖÿ¥ÿßÿ±ŸÉŸäŸÜ ÿßŸÑÿ¢ÿÆÿ±ŸäŸÜ ÿ≥ÿ™ÿ∏Ÿáÿ± ŸáŸÜÿß ÿ®ŸÖÿ¨ÿ±ÿØ ÿ•ÿ±ÿ≥ÿßŸÑŸáÿß
          </div>
        )}
      </div>
      
      <div className="session-footer">
        <Link to="/dashboard" className="btn btn-secondary back-to-dashboard">
          ÿßŸÑÿπŸàÿØÿ© ÿ•ŸÑŸâ ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ
        </Link>
      </div>
    </div>
  );
};

export default SessionPage;
