import React from 'react';
import './PredictionItem.css';

const PredictionItem = ({ prediction, isCurrentUser }) => {
  // التأكد من وجود بيانات المستخدم
  if (!prediction || !prediction.user) {
    console.error('خطأ: بيانات التوقع أو المستخدم غير متوفرة', prediction);
    return null;
  }

  // تنسيق التاريخ والوقت (بالميلادي)
  const formatDateTime = (dateString) => {
    const options = { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    };
    return new Date(dateString).toLocaleDateString('ar', options);
  };

  // توليد حرف أولي للمستخدم
  const getInitial = (username) => {
    return username.charAt(0).toUpperCase();
  };

  // قائمة من الألوان المتباينة المحددة مسبقاً
  const distinctColors = [
    '#FF6B6B', // أحمر فاتح
    '#4D96FF', // أزرق
    '#6BCB77', // أخضر
    '#FFD93D', // أصفر
    '#9B59B6', // بنفسجي
    '#FF9F45', // برتقالي
    '#4ECDC4', // فيروزي
    '#FF78C4', // وردي
    '#7158E2', // أرجواني
    '#3D4152', // رمادي داكن
    '#45AAB8', // أزرق مخضر
    '#FC6471', // وردي محمر
    '#2E8BC0', // أزرق متوسط
    '#59CD90', // أخضر فاتح
    '#F8C537', // أصفر ذهبي
    '#9C73AD', // بنفسجي فاتح
    '#F3722C', // برتقالي داكن
    '#43B5A0', // أخضر مزرق
    '#F25CA2', // وردي غامق
    '#5E60CE'  // أزرق بنفسجي
  ];

  // توليد لون للصورة الرمزية باستخدام معرف المستخدم
  const getAvatarColor = (userId) => {
    // استخدام معرف المستخدم بدلاً من اسم المستخدم
    // تحويل المعرف إلى رقم صحيح
    let hash = 0;
    const idString = userId.toString();
    for (let i = 0; i < idString.length; i++) {
      hash = idString.charCodeAt(i) + ((hash << 5) - hash);
    }
    
    // الحصول على مؤشر للون من القائمة المحددة مسبقاً
    const colorIndex = Math.abs(hash) % distinctColors.length;
    return distinctColors[colorIndex];
  };

  // توليد معرف مختصر
  const getShortId = (id) => {
    if (!id) return '';
    return id.substring(0, 6);
  };

  return (
    <div className={`prediction-item ${isCurrentUser ? 'current-user' : ''}`}>
      <div className="prediction-header">
        <div className="user-info">
          <div 
            className="avatar" 
            style={{ backgroundColor: getAvatarColor(prediction.user._id) }}
          >
            {getInitial(prediction.user.username)}
          </div>
          <div className="user-details">
            <div className="username-container">
              <span className="username-text">{prediction.user.username}</span>
              {isCurrentUser && <span className="user-badge">أنت</span>}
            </div>
            <div className="prediction-time">{formatDateTime(prediction.createdAt)}</div>
          </div>
        </div>
        
        <div className="prediction-meta">
          <div className="prediction-id">#{getShortId(prediction._id)}</div>
        </div>
      </div>
      
      <div className="prediction-content">
        <p>{prediction.text}</p>
      </div>
    </div>
  );
};

export default PredictionItem;
