import React, { useState, useEffect, useContext, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { UserContext } from '../context/UserContext';
import SessionCard from '../components/SessionCard';
import config from '../config';
import './DashboardPage.css';

const DashboardPage = () => {
  const [activeTab, setActiveTab] = useState('join');
  const [sessions, setSessions] = useState([]);
  const [sessionCode, setSessionCode] = useState('');
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  
  const { user } = useContext(UserContext);
  const navigate = useNavigate();
  
  // ÿ¨ŸÑÿ® ÿ¨ŸÑÿ≥ÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ - ŸÖÿπ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ useCallback
  const fetchUserSessions = useCallback(async () => {
    try {
      setLoading(true);
      
      // ÿ™ŸÉŸàŸäŸÜ ÿßŸÑŸáŸäÿØÿ±
      const headers = {
        headers: {
          Authorization: `Bearer ${user.token}`
        }
      };
      
      // ÿ¨ŸÑÿ® ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      const { data } = await axios.get(`${config.API_URL}/sessions/mysessions`, headers);
      
      setSessions(data.sessions);
      setLoading(false);
    } catch (error) {
      setErrorMessage('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ¨ŸÑÿ® ÿßŸÑÿ¨ŸÑÿ≥ÿßÿ™');
      setLoading(false);
    }
  }, [user.token]);
  
  // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¨ŸÑÿ≥ÿßÿ™ ÿπŸÜÿØ ŸÅÿ™ÿ≠ ÿßŸÑÿ™ÿ®ŸàŸäÿ®
  useEffect(() => {
    if (activeTab === 'sessions') {
      fetchUserSessions();
    }
  }, [activeTab, fetchUserSessions]);
  
  // ÿßŸÑÿ™ÿ®ÿØŸäŸÑ ÿ®ŸäŸÜ ÿßŸÑÿ™ÿ®ŸàŸäÿ®ÿßÿ™
  const switchTab = (tab) => {
    setActiveTab(tab);
    setErrorMessage('');
    setSuccessMessage('');
  };
  
  // ŸÖÿπÿßŸÑÿ¨ÿ© ÿ™ÿ∫ŸäŸäÿ± ŸÉŸàÿØ ÿßŸÑÿ¨ŸÑÿ≥ÿ©
  const handleCodeChange = (e) => {
    // ÿ™ÿ≠ŸàŸäŸÑ ÿßŸÑÿ≠ÿ±ŸàŸÅ ÿ•ŸÑŸâ ŸÉÿ®Ÿäÿ±ÿ© Ÿàÿ•ÿ≤ÿßŸÑÿ© ÿßŸÑŸÖÿ≥ÿßŸÅÿßÿ™
    const code = e.target.value.toUpperCase().replace(/\s/g, '');
    setSessionCode(code);
    setErrorMessage('');
    setSuccessMessage('');
  };
  
  // ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿßŸÜÿ∂ŸÖÿßŸÖ ÿ•ŸÑŸâ ÿ¨ŸÑÿ≥ÿ©
  const handleJoinSession = async (e) => {
    e.preventDefault();
    
    // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ•ÿØÿÆÿßŸÑ ÿßŸÑŸÉŸàÿØ
    if (!sessionCode || sessionCode.length !== 6) {
      setErrorMessage('Ÿäÿ±ÿ¨Ÿâ ÿ•ÿØÿÆÿßŸÑ ŸÉŸàÿØ ÿµÿßŸÑÿ≠ ŸÖŸÉŸàŸÜ ŸÖŸÜ 6 ÿ£ÿ≠ÿ±ŸÅ');
      return;
    }
    
    try {
      setLoading(true);
      
      // ÿ™ŸÉŸàŸäŸÜ ÿßŸÑŸáŸäÿØÿ±
      const headers = {
        headers: {
          Authorization: `Bearer ${user.token}`
        }
      };
      
      // ÿ•ÿ±ÿ≥ÿßŸÑ ÿ∑ŸÑÿ® ÿßŸÑÿßŸÜÿ∂ŸÖÿßŸÖ
      const { data } = await axios.post(
        `${config.API_URL}/sessions/join`, 
        { code: sessionCode }, 
        headers
      );
      
      setSuccessMessage('ÿ™ŸÖ ÿßŸÑÿßŸÜÿ∂ŸÖÿßŸÖ ÿ•ŸÑŸâ ÿßŸÑÿ¨ŸÑÿ≥ÿ© ÿ®ŸÜÿ¨ÿßÿ≠');
      setLoading(false);
      
      // ÿßŸÑÿ™Ÿàÿ¨ŸäŸá ÿ•ŸÑŸâ ÿµŸÅÿ≠ÿ© ÿßŸÑÿ¨ŸÑÿ≥ÿ© ÿ®ÿπÿØ ÿ´ÿßŸÜŸäÿ©
      setTimeout(() => {
        navigate(`/session/${data.session._id}`);
      }, 1000);
    } catch (error) {
      setErrorMessage(error.response?.data?.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿßŸÜÿ∂ŸÖÿßŸÖ ŸÑŸÑŸÇŸäŸÖ');
      setLoading(false);
    }
  };
  
  return (
    <div className="dashboard-page">
      <div className="dashboard-header">
        <h1 className="dashboard-title">ÿßŸÑÿØÿßÿ¥ÿ®Ÿàÿ±ÿØ</h1>
      </div>
      
      <div className="dashboard-tabs">
        <div 
          className={`dashboard-tab ${activeTab === 'join' ? 'active' : ''}`}
          onClick={() => switchTab('join')}
        >
          ÿßŸÜÿ∂ŸÖ/ÿ£ŸÜÿ¥ÿ¶ ÿ¨ŸÑÿ≥ÿ©
        </div>
        <div 
          className={`dashboard-tab ${activeTab === 'sessions' ? 'active' : ''}`}
          onClick={() => switchTab('sessions')}
        >
          ÿ¨ŸÑÿ≥ÿßÿ™Ÿä
        </div>
        <div className={`tab-indicator ${activeTab === 'sessions' ? 'right' : ''}`}></div>
      </div>
      
      <div className="dashboard-content">
        {activeTab === 'join' ? (
          <div className="join-content">
            <div className="join-section">
              <h3 className="subsection-title">ŸÉŸàÿØ ÿßŸÑÿ¨ŸÑÿ≥ÿ©</h3>
              
              {errorMessage && (
                <div className="alert alert-error">{errorMessage}</div>
              )}
              
              {successMessage && (
                <div className="alert alert-success">{successMessage}</div>
              )}
              
              <form onSubmit={handleJoinSession}>
                <div className="form-group">
                  <input
                    type="text"
                    className="form-control session-code-input"
                    placeholder="ÿßŸÉÿ™ÿ® ÿßŸÑŸÉŸàÿØ ŸáŸÜÿß"
                    value={sessionCode}
                    onChange={handleCodeChange}
                    maxLength={6}
                  />
                </div>
                
                <button 
                  type="submit" 
                  className="btn btn-primary btn-block join-btn"
                  disabled={loading}
                >
                  {loading ? 'ÿ¨ÿßÿ±Ÿê ÿßŸÑÿßŸÜÿ∂ŸÖÿßŸÖ...' : 'ÿßŸÜÿ∂ŸÖ ÿ•ŸÑŸâ ÿßŸÑÿ¨ŸÑÿ≥ÿ©'}
                </button>
              </form>
              
              <div className="divider">
                <span>ÿ£Ÿà</span>
              </div>
              
              <Link to="/create-session" className="btn btn-secondary btn-block create-btn">
                ÿ•ŸÜÿ¥ÿßÿ° ÿ¨ŸÑÿ≥ÿ© ÿ¨ÿØŸäÿØÿ©
              </Link>
            </div>
          </div>
        ) : (
          <div className="sessions-content">
            <h2 className="section-title">ÿ¨ŸÑÿ≥ÿßÿ™Ÿä</h2>
            
            {loading ? (
              <div className="loading-container">
                <div className="loading-spinner"></div>
                <p className="loading-text">ÿ¨ÿßÿ±Ÿê ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¨ŸÑÿ≥ÿßÿ™...</p>
              </div>
            ) : (
              <>
                {sessions.length === 0 ? (
                  <div className="empty-state">
                    <div className="empty-icon">üìã</div>
